    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        Funciones funcion = new Funciones();
        Funcion_gato gato = new Funcion_gato();
        Auxilliar2 otro = new Auxilliar2();
        String name1 = name.getText();
        String password1 = password.getText();
        if(name1.equals("")||password1.equals("")){
            JOptionPane.showConfirmDialog(null, "Debe de llenar todas casillas", "Cuadro de dialogo", 
                    JOptionPane.CLOSED_OPTION, JOptionPane.ERROR_MESSAGE);
            name.setText("");
            password.setText("");
        }
        else{
            try {
                if(funcion.buscarjugador(name1, password1)){
                    otro.setVisible(true);
                    this.setVisible(false);
                    gato.player1(name1);
                }
                else{
                    JOptionPane.showMessageDialog(null, "El usuario no esta registrado");
                    name.setText("");
                    password.setText("");
                }
            } catch (IOException | ClassNotFoundException ex) {
                Logger.getLogger(Auxiliar.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        Menu menu = new Menu();
        int opcion = JOptionPane.showConfirmDialog(null, "Esta seguro de salir ","Cuadro de dialogo", 
                                                JOptionPane.OK_CANCEL_OPTION, JOptionPane.INFORMATION_MESSAGE);
        if(opcion==0){
            menu.setVisible(true);
            this.setVisible(false);
        }
    }//GEN-LAST:event_jButton2ActionPerformed

