    private void CancelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CancelActionPerformed
        int opcion = JOptionPane.showConfirmDialog(null, "Esta seguro de salir \n"
                                                +"(No se guardaran los datos)","Cuadro de dialogo", 
                                                JOptionPane.OK_CANCEL_OPTION, JOptionPane.INFORMATION_MESSAGE);
        if(opcion==0){
            menu.setVisible(true);
            this.setVisible(false);
        }
    }//GEN-LAST:event_CancelActionPerformed

    private void DeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DeleteActionPerformed
        if(name.getText().equals("")||password.getText().equals("")){
            JOptionPane.showConfirmDialog(null, "Debe de llenar todas las casillas", "Cuadro de dialogo", 
                    JOptionPane.CLOSED_OPTION, JOptionPane.ERROR_MESSAGE);
            name.setText("");
            password.setText("");
        }
        else{
            String Name = name.getText();
            String Password = password.getText();
            try {
                if(funcion.eliminar_registro(Name, Password)){
                    JOptionPane.showConfirmDialog(null, "El jugador ha sido eliminado con exito", "Cuadro de dialogo", 
                    JOptionPane.CLOSED_OPTION, JOptionPane.INFORMATION_MESSAGE);
                    menu.setVisible(true);
                    this.setVisible(false);
                }
                else{
                    JOptionPane.showConfirmDialog(null, "El jugador no ha sido encontrado", "Cuadro de dialogo", 
                    JOptionPane.CLOSED_OPTION, JOptionPane.INFORMATION_MESSAGE);
                    name.setText("");
                    password.setText("");
                }
            } catch (IOException | ClassNotFoundException ex) {
                Logger.getLogger(Elminar_jugador.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_DeleteActionPerformed

